/** Unit */
function nxtChgUnit(curr, main, name, unit){

    if(!curr.classList.contains('active')){
        let getClUl = curr.closest('.nxt-unit-btn-group');
        if(getClUl){
            let getAct = getClUl.querySelector('.active');
            if(getAct){
                getAct.classList.remove('active');
            }
            curr.classList.add('active');
        }
    }
    
    if(main){
        globalNxtCss[main][name] =  Object.assign({},globalNxtCss[main][name], { 'unit': unit });
    }else{
        globalNxtCss[name] =  Object.assign({},globalNxtCss[name], { 'unit': unit });
    }
}
