/** Toggle Field */
function nxtToggle(tgl){
    let tName = tgl.getAttribute('name');
    if(tgl.classList.contains('is-checked')){
        tgl.classList.remove('is-checked');
        globalNxtCss = Object.assign({[tName] : false}, globalNxtCss);
    }else{
        tgl.classList.add('is-checked');
        globalNxtCss = Object.assign({[tName] : true}, globalNxtCss);
    }
}


/** Select Field */
function nxtSelect(slct){
    let sName = slct.getAttribute('name'),
        imgComp = slct.getAttribute('data-imgComp'),
        mainComp = slct.getAttribute('data-main'),
        selectedType = slct.value;
    if(mainComp){
        if(imgComp){
            globalNxtCss[mainComp][imgComp] = Object.assign({},globalNxtCss[mainComp][imgComp], { [sName] : selectedType });
        }else{
            globalNxtCss[mainComp] = Object.assign({},globalNxtCss[mainComp], { [sName] : selectedType });
        }
    }else{
        if(imgComp){
            globalNxtCss = Object.assign({[imgComp] : {[sName] : selectedType}}, globalNxtCss);
        }else{
            globalNxtCss[sName] = selectedType;
        }   
    }
}


