/** Border Field */
function nxtBorder(borderVal,mainName){
    let borderClass = borderVal.closest('.nxt-field-border'),
        borderPopup = borderClass.querySelector('.nxt-border-popup'),
        borderStyle = borderClass.querySelectorAll('.nxt-button');
        
    if(!borderVal.classList.contains('active')){
        borderVal.classList.add('active');
        borderPopup.classList.add('active');
        borderStyle.forEach(self=> {
            self.classList.remove('active');
        })
        borderVal.classList.add('active');
    }

    if(globalNxtCss[mainName]['open']==0){
        let rstClass = borderClass.querySelector('.nxt-open-clear');
        if(rstClass){
            rstClass.classList.add('active');
        }
        globalNxtCss[mainName]['open'] = 1;
    }
    
    let borderDatatype = borderVal.getAttribute('name'),
        borderType = borderVal.getAttribute('label'),
        borderMain = borderVal.closest('.nxt-field-border'),
        getborderVal = borderMain.getAttribute('data-attr');
    getborderVal = JSON.parse(getborderVal);
    if(globalNxtCss[mainName]==undefined){
        globalNxtCss[mainName] = getborderVal;
    }
    globalNxtCss[mainName] = Object.assign({},globalNxtCss[mainName], { [borderDatatype] : borderType });
}

