function nxtFFList(ff, name, main){
    let getInp = ff.querySelector('.nxt-font-filter'),
        fontIcon = ff.querySelector('.nxt-font-updown-icon');
    if(ff.classList.contains('active')){
        ff.classList.remove('active');
        fontIcon.querySelector('svg').style = '';
        ff.nextSibling.remove();
    }else{
        ff.classList.add('active');
        fontIcon.querySelector('svg').style.transform = 'rotate(-180deg)';
        ff.insertAdjacentHTML('afterend', '<div class="nxt-fonts-list-wrap"><ul class="nxt-font-list-inner"></ul></div>');

        let getopt = ff.closest('.nxt-font-family-opt'),
            getLIstUl = getopt.querySelector('.nxt-font-list-inner');

        newfonts[0].options.forEach((item) => {
            let li = document.createElement("li");
            let selFont = '';
            if(globalNxtCss[main][name]["family"] && globalNxtCss[main][name]["family"] == item.n){
                selFont = 'selected-font';
            }
            li.className = "nxt-font-list-opt "+selFont;
            li.addEventListener('click', function(event) {
                getInp.placeholder = item.n;
                getInp.value = '';
                globalNxtCss[main] = Object.assign({},globalNxtCss[main], { [name] : {'family': item.n, 'type': item.c} });

                let selClass = getLIstUl.querySelector('.selected-font');
                if(selClass){
                    selClass.classList.remove('selected-font');
                }
                event.currentTarget.classList.add('selected-font');

                ff.classList.remove('active');
                fontIcon.querySelector('svg').style = '';
                ff.nextSibling.remove();
            });
            li.innerText = item.n;
            getLIstUl.appendChild(li);
        });

    }
}
function nxtFWList(fw, name, main){
    let getInp = fw.querySelector('.nxt-font-weight-val'),
        fontIcon = fw.querySelector('.nxt-font-updown-icon');
    if(fw.classList.contains('active')){
        fw.classList.remove('active');
        fontIcon.querySelector('svg').style = '';
        fw.nextSibling.remove();
    }else{
        fw.classList.add('active');
        fontIcon.querySelector('svg').style.transform = 'rotate(-180deg)';
        fw.insertAdjacentHTML('afterend', '<div class="nxt-weights-list-wrap"><ul class="nxt-weight-list-inner"></ul></div>');

        let getopt = fw.closest('.nxt-font-weight-opt'),
            getLIstUl = getopt.querySelector('.nxt-weight-list-inner');
        newfonts[0].options.forEach((item) => {
            
            if(globalNxtCss[main]['font_family']["family"] == item.n || globalNxtCss[main]['font_family']["family"] == ''){
                item.v.forEach((item) => {
                    let li = document.createElement("li");
                    let selFont = '';
                    if(globalNxtCss[main][name] && globalNxtCss[main][name] == item){
                        selFont = 'selected-weight';
                    }
                    li.className = "nxt-weight-list-opt "+selFont;
                    li.addEventListener('click', function(event) {
                        getInp.innerText = item;
                        globalNxtCss[main] = Object.assign({},globalNxtCss[main], { [name] : item });
                        let selClass = getLIstUl.querySelector('.selected-weight');
                        if(selClass){
                            selClass.classList.remove('selected-weight');
                        }
                        event.currentTarget.classList.add('selected-weight');
                        fw.classList.remove('active');
                        fontIcon.querySelector('svg').style = '';
                        fw.nextSibling.remove();
                    });
                    li.innerText = item;
                    getLIstUl.appendChild(li);
                });
            }
        });
    }
}



function nxtFFSearch(ser){
    let filter = ser.value.toUpperCase();

    let mainTypo = ser.closest('.nxt-field-typography'),
        liList = mainTypo.querySelectorAll('.nxt-font-list-opt');
    newfonts[0].options.forEach((item, index) => {
        if (item['n'].toUpperCase().indexOf(filter) > -1) {
            liList[index].style.display = "";
        } else {
            liList[index].style.display = "none";
        }
    });
}

