/** Background Field */
function nxtBackground(bgVal,mainName) {

    let bgClass = bgVal.closest('.nxt-field-background');

    if(bgVal.classList.contains('bg-color')){
        let bgPopup = bgClass.querySelector('.nxt-bg-color');
        bgStyle(bgVal,bgClass,bgPopup);
        globalNxtCss[mainName]['bgType'] = 'color';
    }else if(bgVal.classList.contains('bg-gradient')){
        let bgPopup = bgClass.querySelector('.nxt-bg-gradient');
        bgStyle(bgVal,bgClass,bgPopup);
        globalNxtCss[mainName]['bgType'] = 'gradient';
    }else if(bgVal.classList.contains('bg-image')){
        let bgPopup = bgClass.querySelector('.nxt-bg-image');
        bgStyle(bgVal,bgClass,bgPopup);
        globalNxtCss[mainName]['bgType'] = 'image';
    }

    if(globalNxtCss[mainName]['open']==0){
        let rstClass = bgClass.querySelector('.nxt-open-clear');
        if(rstClass){
            rstClass.classList.add('active');
        }
        globalNxtCss[mainName]['open'] = 1;
    }


    function bgStyle(bgVal,bgClass,bgPopup){
        let bgAllBtn = bgClass.querySelectorAll('.nxt-button'),
            bgAll = bgClass.querySelectorAll('.nxt-d-flex');

        if(!bgVal.classList.contains('active')){
            bgAllBtn.forEach(self => {
                self.classList.remove('active');
            })
            bgAll.forEach(self => {
                self.classList.remove('active');
            })
            bgVal.classList.add('active');
            bgPopup.classList.add('active');
        }
    }
}

/** Tab-tabs Field */
function nxtTabs(tabsVal){
    let tabsMain = tabsVal.closest('.nxt-inspect-tabs'),
        tabsAll = tabsMain.querySelectorAll('.nxt-tab-menu'),
        getDI = tabsVal.getAttribute('data-id'),
        tabItems = tabsMain.querySelectorAll('.nxt-tab-items');
    if(!tabsVal.classList.contains('active-tab')){
        tabsAll.forEach((tv) => {
            tv.classList.remove('active-tab');
        })
        tabsVal.classList.add('active-tab');
        
        if(tabItems){
            tabItems.forEach((ti) => {
                if( ti.classList.contains('active')){
                    ti.classList.remove('active');
                }
                if(getDI){
                    if(ti.classList.contains('tab-'+getDI)){
                        ti.classList.add('active');
                    }
                }
            })
        }
    }
}

/** Background */
function nxtGradtype(type,name, main){
    let selectedType = type.value;

    let closeAngle = type.closest('.nxt-field-gradient');

    let gAngle = closeAngle.querySelector('.nxt-field-gradient-angle');

    if(selectedType=='radial'){
        if(gAngle){
            gAngle.classList.add('angle-hide');
        }
    }else{
        if(gAngle){
            gAngle.classList.remove('angle-hide');
        }
    }

    if(main){
        globalNxtCss[main][name] = Object.assign({},globalNxtCss[main][name], {type: selectedType });
    }else{
        globalNxtCss[name] = Object.assign({},globalNxtCss[name], { type: selectedType });
    }
}

