/** Background Initial */
function nxtBGInitial(){
    let allBG = document.querySelectorAll('.nxt-field-background');
    if(allBG){
        allBG.forEach((ad)=>{
            if(!ad.classList.contains('not-a-main')){
                let bgName = ad.getAttribute('data-name'),
                    allData = ad.getAttribute('data-attr');
                allData = JSON.parse(allData);
                let typeBG = allData.bgType;
                if(typeBG && allData.open){
                    let btn = ad.querySelector('.bg-'+typeBG),
                        popup = ad.querySelector('.nxt-bg-'+typeBG);
                    if(btn){
                        btn.classList.add('active');
                    }
                    if(popup){
                        popup.classList.add('active');
                    }
                }
                if(allData.background_gradient.type && allData.background_gradient.type == 'radial'){
                    let gAngle = ad.querySelector('.nxt-field-gradient-angle');
                        gAngle.classList.add('angle-hide');
                }
                globalNxtCss = Object.assign({[bgName] : allData}, globalNxtCss);
            }
        });
    }
}

/** Reset Button */
function resetVal(reset,name){
    if(globalNxtCss[name]['open'] && reset.classList.contains('active')){
        reset.classList.remove('active');
        globalNxtCss[name] = Object.assign({},globalNxtCss[name], { 'open': 0 });
    }

    let resetBorder = reset.closest('.nxt-field-border');
    if(resetBorder){
        let activeBtn = resetBorder.querySelector('.nxt-button.active'),
            activePopup = resetBorder.querySelector('.nxt-border-popup.active');
        activePopup.classList.remove('active');
        activeBtn.classList.remove('active');
    }

    let resetBg = reset.closest('.nxt-field-background');
    if(resetBg){
        let activeBtn = resetBg.querySelector('.nxt-button.active'),
            activeBg =  resetBg.querySelector('.nxt-d-flex.active');
        if(activeBtn, activeBg){
            activeBtn.classList.remove('active');
            activeBg.classList.remove('active');
        }
    }

    let resetTypo = reset.closest('.nxt-field-typography');
    if(resetTypo){
        let typoPopup = resetTypo.querySelector('.nxt-typo-popup');
        if(typoPopup.classList.contains('active')){
            typoPopup.classList.remove('active');
        }

        let tpSett = resetTypo.querySelector('.nxt-typo-settings');
        if(tpSett && tpSett.classList.contains('active')){
            tpSett.classList.remove('active');
        }
    }
}

